'use strict';

module.exports = function serializePath(item) {
  return item.path.reduce(function (target, part, i) {
    var isIndex = typeof part === 'number';
    var seperator = i === 0 ? '' : '.';
    var add = isIndex ? `[${part}]` : `${seperator}${part}`;
    return `${target}${add}`;
  }, '');
};